-- ----------------------------
-- Table structure for cms_friend_link
-- ----------------------------
DROP TABLE IF EXISTS `cms_friend_link`;
CREATE TABLE `cms_friend_link`  (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '友情链接ID',
  `name` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '友链名称',
  `link` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '链接',
  `description` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '站点描述',
  `logo` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT 'logo素材',
  `audit_state` smallint(6) NULL DEFAULT 0 COMMENT '状态 0待审核 1通过 2未通过',
  `status` smallint(6) NULL DEFAULT 0 COMMENT '状态 0正常 1停用',
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 4 CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = Dynamic;

