-- ----------------------------
-- Table structure for cms_link
-- ----------------------------
DROP TABLE IF EXISTS `cms_link`;
CREATE TABLE `cms_link`  (
  `link_id` int(11) NOT NULL AUTO_INCREMENT COMMENT '链接ID',
  `link_type` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '链接分类',
  `link_name` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '链接名称',
  `keywords` varchar(100) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '关键词',
  `link` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '链接',
  `description` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '描述',
  `logo` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT 'Logo',
  `audit_state` smallint(6) NULL DEFAULT 0 COMMENT '审核状态',
  `detail` longtext CHARACTER SET utf8 COLLATE utf8_general_ci NULL COMMENT '详情',
  `sort` int(11) NULL DEFAULT 0 COMMENT '排序',
  `up_vote` int(11) NULL DEFAULT 0 COMMENT '点赞数',
  `comment_flag` smallint(6) NULL DEFAULT 1 COMMENT '允许评论',
  `status` smallint(6) NULL DEFAULT 0 COMMENT '状态 0正常 1停用',
  PRIMARY KEY (`link_id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 1 CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = Dynamic;

-- ----------------------------
-- Table structure for cms_link_type
-- ----------------------------
DROP TABLE IF EXISTS `cms_link_type`;
CREATE TABLE `cms_link_type`  (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT 'ID',
  `link_type` varchar(100) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '链接分类',
  `link_type_name` varchar(100) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '链接分类名称',
  `description` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '描述',
  `cover_image` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '封面图片',
  `sort` smallint(6) NULL DEFAULT 0 COMMENT '排序',
  `status` smallint(6) NULL DEFAULT 0 COMMENT '状态 0正常 1停用',
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 4 CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = Dynamic;

