-- ----------------------------
-- Table structure for cms_resource
-- ----------------------------
DROP TABLE IF EXISTS `cms_resource`;
CREATE TABLE `cms_resource`  (
  `id` varchar(64) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '资源ID',
  `resource_region` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '资源专区',
  `title` varchar(100) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '资源名称',
  `type` varchar(10) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '资源类型',
  `description` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '资源描述',
  `detail` longtext CHARACTER SET utf8 COLLATE utf8_general_ci NULL COMMENT '详情',
  `size` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '资源大小',
  `save_path` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '保存路径',
  `cover_image` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '缩略图',
  `audit_state` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT '0' COMMENT '审核状态 0待审批2未通过1通过',
  `score` smallint(6) NULL DEFAULT NULL COMMENT '评分 1-5',
  `download_type` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT '0' COMMENT '下载类型 0直接下载 1登录后下载 2关注下载',
  `favourite_count` int(11) NULL DEFAULT 0 COMMENT '收藏数',
  `download_count` int(11) NULL DEFAULT 0 COMMENT '下载次数',
  `hit` int(11) NULL DEFAULT 0 COMMENT '点击数',
  `up_vote` int(11) NULL DEFAULT 0 COMMENT '点赞数',
  `free` smallint(6) NULL DEFAULT NULL COMMENT '付费 0免费 1收费',
  `pay_type` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '付费类型 1 积分 2扫码支付',
  `cost` int(11) NULL DEFAULT 0 COMMENT '花费多少积分',
  `tag_ids` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '标签',
  `user_id` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '上传人用户ID',
  `user_name` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '上传人',
  `create_time` datetime(0) NULL DEFAULT NULL COMMENT '上传日期',
  `update_time` datetime(0) NULL DEFAULT NULL COMMENT '上传者 id',
  `status` smallint(6) NULL DEFAULT 0 COMMENT '状态 0 正常 1停用',
  `remark` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '备注',
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = Dynamic;

-- ----------------------------
-- Records of cms_resource
-- ----------------------------
INSERT INTO `cms_resource` VALUES ('2021112319350975585661', 'recommend', 'ASP.NET MVC通用权限管理系统源代码开源发布v1.1', '1', '系统追求原始精简的原则在执行效率上更快，采用bootstrap为前台开发展示UI，Web Api主要负责前端的逻辑交互，再结合jQuery Ajax+Web Api进行提交数据请求，系统免费开源。', '<p>一、Angel工作室简单通用权限系统简介</p>\n<p>&nbsp;&nbsp;&nbsp;AngelRM（Asp.net MVC）是基于asp.net(C#)MVC打造后端原生态代码+前端bootstrap+ztree+lodash+jquery技术，系统追求原始精简的原则在执行效率上更快，采用bootstrap为前台开发展示UI，Web Api主要负责前端的逻辑交互，再结合jQuery Ajax+Web Api进行提交数据请求。</p>\n<p>二、框架特色</p>\n<p>1、为了数据方便读写，语言的成熟性，选择asp.net mvc开发效率更高效率更快；</p>\n<p>2、系统架构采用：耦合性低、重用性高、部署快、可维护性高等优点的MVC框架进行搭建；</p>\n<p>3、系统配置文件数据采用HttpRuntime.Cache进行缓存，使得程序在运行中效率更高、速度更快；</p>\n<p>4、数据库的选型，根据系统的数据规模与需求方的相关要求，综合多方考量，调研多种数据库后选定mysql为系统数据库；</p>\n<p>5、运行环境根据功能模块的特点，选型window server2008+IIS，部署更简洁；</p>\n<p>6、使用报表导出开源组件NPOI可以在没有安装Office的情况下对Word或Excel文档进行读写操作；</p>\n<p>7、为了兼容更多的浏览器，让用户界面使用更友好。我们选择了响应式布局框架bootstrap；</p>\n<p>8、系统报表我们采用了echarts开源软件，并且我们提供了非常炫酷的图形界面，特色是地图，另外还提供了柱状图、折线图、饼图、气泡图及四象限图等；</p>\n<p>9、系统采用bootstrap响应式布局，这样面对不同分辨率设备灵活性强能够快捷解决多设备显示适应问题；</p>\n<p>10、系统基于ASP.NET(C#) MVC +web api+Bootstrap +Jquery+ MYSQL/SQL SERVER前端采用响应式布局对页面兼容性显著提高；</p>\n<p>系统优势：</p>\n<p>代码可以满足大部分开发者的需求，让开发人员能省不少时间</p>\n<p>开源下载地址&nbsp;</p>\n<p>Gitee地址：https://gitee.com/angelcms/AngelRM_MVC</p>\n<p>系统演示图：</p>\n<p><img src=\"https://img2020.cnblogs.com/blog/516621/202105/516621-20210518134027451-806863202.jpg\" alt=\"\"></p>\n<p><img src=\"https://img2020.cnblogs.com/blog/516621/202105/516621-20210518134231220-1662563360.jpg\" alt=\"\"></p>\n<p><img src=\"https://img2020.cnblogs.com/blog/516621/202105/516621-20210518134258538-2109971796.jpg\" alt=\"\"></p>\n<p><img src=\"https://img2020.cnblogs.com/blog/516621/202105/516621-20210518134325270-1227113377.jpg\" alt=\"\"></p>\n<p><img src=\"https://img2020.cnblogs.com/blog/516621/202105/516621-20210518134339388-1285948521.jpg\" alt=\"\"></p>\n<p><img src=\"https://img2020.cnblogs.com/blog/516621/202105/516621-20210518134404043-156779797.jpg\" alt=\"\"></p>\n<p>等等还有一些页面我就不一一上传了</p>\n<p><br></p>\n<p><br></p>', NULL, NULL, '/images/cover/1561132050443308.jpg', '1', 3, '0', 0, 0, 3, 2, 0, '1', 0, NULL, '1', 'alcedo', '2021-11-23 19:35:10', '2022-05-29 21:30:02', 0, NULL);
INSERT INTO `cms_resource` VALUES ('2021112319350975585662', 'recommend', 'Angel工作室ASP.NET(AngelExam)开源驾校考试系统正式发布', '1', '前台考试界面简单易用；后台采用外观极简美观却又不失饱满的内在的layui 框架，体积轻盈，组件丰盈，从核心代码到 API 的每一处细节都经过精心雕琢，非常适合Web界面的快速开发。', '<p>一、Angel工作室ASP.NET(AngelExam)开源驾校考试系统简介</p>\n<p>&nbsp;&nbsp;&nbsp;Angel工作室ASP.NET(AngelExam)驾校考试系统是基于asp.net mvc4.5(C#)+前端bootstrap+jquery+layui等技术，数据存储采用性能可靠稳定的sql server数据库 。<br>前台页面采用无刷新技术基于mvc web api接口调用逻辑进行交互，前台考试界面简单易用；后台采用外观极简美观却又不失饱满的内在的layui 框架，体积轻盈，组件丰盈，从核心代码到 API 的每一处细节都经过精心雕琢，非常适合Web界面的快速开发。</p>\n<p>二、考试系统特色</p>\n<p>1、为了数据方便读写，语言的成熟性，选择asp.net mvc开发效率更高效率更快；<br>2、系统架构采用：耦合性低、重用性高、部署快、可维护性高等优点的三层框架进行搭建；<br>3、系统配置文件数据采用HttpRuntime.Cache进行缓存，使得程序在运行中效率更高、速度更快；<br>4、数据库的选型，根据系统的考试题库数据规模多方面考虑，最终调研多种数据库后选定sql server为系统数据库；<br>5、运行环境根据功能模块的特点，选型window server+IIS，部署方面更简单；</p>\n<p>三、系统优势：</p>\n<p>1、系统基于asp.net mvc开发,目前支持90%以上的空间，附件上数据库（sql server2008r2）或者直接运行.sql文件，用户可以直接传入空间使用。<br>2、系统支持多管理账号、添加多学员信息、添加题库分类、添加多类型题型等驾校考试系统功能。<br>3、该考试系统题库可以灵活新增、修改、删除等操作，其中包括单选、多选、判断等题型。<br>4、考试系统内置考试模拟题型模拟筛选计算逻辑，后台操作员可以对各题库分类抽取数灵活设置，使得题库模拟更真实逼真。<br>5、驾校考试系统可以满足大部分驾校的需求，让想需要一套自主运营的驾校最好的选择。</p>\n<p>2021-03-15</p>\n<p>四 、更新如下：</p>\n<p>1、新增产品颜色上传功能，让产品可以支持多种颜色进行预览；<br>2、新增产品评价功能，弥补了以往驾校考试系统无产品评论的功能；<br>3、系统登录页面新增验证校验过程，这样做到输入验证码时时验证；<br>4、新增题库明细信息相关产品信息，该功能可以在后台进行灵活设置；<br>5、新增题库视频讲解说明，学员可以对每一题进行视频观看；<br>6、优化系统加载页面提示数据加载功能。</p>\n<p><strong>系统演示如下</strong>：</p>\n<p>系统后台</p>\n<p><img src=\"https://img2020.cnblogs.com/blog/516621/202106/516621-20210607210953648-1895085012.jpg\" alt=\"\"></p>\n<p><img src=\"https://img2020.cnblogs.com/blog/516621/202106/516621-20210607211027763-1830035457.jpg\" alt=\"\"></p>\n<p><img src=\"https://img2020.cnblogs.com/blog/516621/202106/516621-20210607211047338-541850566.jpg\" alt=\"\"></p>\n<p><img src=\"https://img2020.cnblogs.com/blog/516621/202106/516621-20210607211106015-2077971041.jpg\" alt=\"\"></p>\n<p><img src=\"https://img2020.cnblogs.com/blog/516621/202106/516621-20210607211128215-901933652.jpg\" alt=\"\"></p>\n<p><img src=\"https://img2020.cnblogs.com/blog/516621/202106/516621-20210607211141058-1167802726.jpg\" alt=\"\"></p>\n<p>前端web考试界面</p>\n<p><img src=\"https://img2020.cnblogs.com/blog/516621/202106/516621-20210607211402553-1958615702.jpg\" alt=\"\"></p>\n<p><img src=\"https://img2020.cnblogs.com/blog/516621/202106/516621-20210607211435466-397207666.jpg\" alt=\"\"></p>\n<p><img src=\"https://img2020.cnblogs.com/blog/516621/202106/516621-20210607211447997-2056573803.jpg\" alt=\"\"></p>\n<p><img src=\"https://img2020.cnblogs.com/blog/516621/202106/516621-20210607211537993-880666706.jpg\" alt=\"\"></p>\n<p><img src=\"https://img2020.cnblogs.com/blog/516621/202106/516621-20210607211547678-458917096.jpg\" alt=\"\"></p>\n<p><img src=\"https://img2020.cnblogs.com/blog/516621/202106/516621-20210607211557429-1091781159.jpg\" alt=\"\"></p>\n<p>&nbsp;开源系统希望对大家有帮助！！&nbsp;</p>\n<p><br></p>', '', '', '/images/cover/nice.jpg', '1', 3, '0', 0, 0, 4, 2, 0, '1', 0, '', '1', 'alcedo', '2021-11-23 19:35:10', '2022-04-30 21:43:51', 0, '');
INSERT INTO `cms_resource` VALUES ('2021112319350975585663', 'recommend', 'ASP.NET MVC通用权限管理系统源代码开源发布v1.2', '1', '系统追求原始精简的原则在执行效率上更快，采用bootstrap为前台开发展示UI，Web Api主要负责前端的逻辑交互，再结合jQuery Ajax+Web Api进行提交数据请求，系统免费开源。', '<p>一、asp.net mvc&nbsp;通用权限管理系统(响应布局)源码主要以下特点：</p>\n<p>&nbsp;&nbsp;&nbsp;AngelRM（Asp.net MVC）是基于asp.net(C#)MVC+前端bootstrap+ztree+lodash+jquery技术，采用bootstrap为前台开发展示UI，Web Api主要负责前端的逻辑交互，再结合jQuery Ajax+Web Api进行提交数据请求。</p>\n<p>二、框架特色</p>\n<p>1、为了数据方便读写，语言的成熟性，选择asp.net开发效率更高效率更快；<br>2、系统架构采用：耦合性低、重用性高、部署快、可维护性高等优点的MVC框架进行搭建；<br>3、系统配置文件数据采用HttpRuntime.Cache进行缓存，使得程序在运行中效率更高、速度更快；<br>4、数据库的选型，根据系统的数据规模与需求方的相关要求，综合多方考量，调研多种数据库后选定mysql为系统数据库；<br>5、运行环境根据功能模块的特点，选型window server2008+IIS，部署更简洁；<br>6、使用报表导出开源组件NPOI可以在没有安装Office的情况下对Word或Excel文档进行读写操作；<br>7、为了兼容更多的浏览器，让用户界面使用更友好。我们选择了响应式布局框架bootstrap；<br>8、系统报表我们采用了echarts开源软件，并且我们提供了非常炫酷的图形界面，特色是地图，另外还提供了柱状图、折线图、饼图、气泡图及四象限图等；<br>9、系统采用bootstrap响应式布局，这样面对不同分辨率设备灵活性强能够快捷解决多设备显示适应问题；</p>\n<p><span>&nbsp;系统优势：</span></p>\n<p><span>代码可以满足大部分开发者的需求，让开发人员能省不少时间。前两天有的小伙伴说我发布的系统图片不太清晰，接下来发几张高清图</span></p>\n<p>三、界面展示如下：</p>\n<p>2.0较1.0版本新增和更新了一些代码</p>\n<p>1、新增文件上传功能实例，保证文件能够无误上传保存并下载；</p>\n<p>2、优化后台系统框架代码，删除以前大项目其它无用功能；</p>\n<p>3、新增页面代码JS、css文件的压缩，使得系统运行速度更快；</p>\n<p>4、系统登录页面新增验证校验过程，这样做到输入验证码时时验证；</p>\n<p>5、优化系统加载页面提示数据加载功能。</p>\n<p>登录页面：</p>\n<p><img src=\"https://img2018.cnblogs.com/common/516621/202002/516621-20200227133609133-1833734207.jpg\" alt=\"\"></p>\n<p>主界面：</p>\n<p><img src=\"https://img2018.cnblogs.com/common/516621/202002/516621-20200227133654250-569018534.jpg\" alt=\"\"></p>\n<p>菜单管理列表：</p>\n<p><img src=\"https://img2018.cnblogs.com/common/516621/202002/516621-20200227133929796-1632802414.jpg\" alt=\"\"></p>\n<p>部门管理：</p>\n<p><img src=\"https://img2018.cnblogs.com/common/516621/202002/516621-20200227134002922-815873456.jpg\" alt=\"\"></p>\n<p>上传文件：</p>\n<p><img src=\"https://img2018.cnblogs.com/common/516621/202002/516621-20200227134039125-118528278.jpg\" alt=\"\"></p>\n<p><em>角色管理：</em></p>\n<p>&nbsp;</p>\n<p><em><img src=\"https://img2018.cnblogs.com/common/516621/202002/516621-20200227134117933-130558068.jpg\" alt=\"\"><br></em></p>\n<p><em>下载页面管理：</em></p>\n<p><em><img src=\"https://img2018.cnblogs.com/common/516621/202002/516621-20200227134147153-395450699.jpg\" alt=\"\"></em></p>\n<p><em><img src=\"https://img2018.cnblogs.com/common/516621/202002/516621-20200227134208399-1028963959.jpg\" alt=\"\"></em></p>\n<p><em><img src=\"https://img2018.cnblogs.com/common/516621/202002/516621-20200227134220918-925256055.jpg\" alt=\"\"></em></p>\n<p><em>等等还有一些页面我就不一一上传了</em></p>\n<p>技术交流可加QQ群：97157124</p>\n<p>Gitee地址：<a href=\"https://gitee.com/angelcms/AngelRM_MVC\">https://gitee.com/angelcms/AngelRM_MVC</a></p>\n<p><br></p>', '', '', '/images/cover/1561132050443308.jpg', '1', 3, '0', 0, 0, 2, 2, 0, '1', 0, '', '1', 'alcedo', '2021-11-23 19:35:10', '2022-04-30 21:48:11', 0, '');
INSERT INTO `cms_resource` VALUES ('2021112319403531361647', 'recommend', 'ASP.NET MVC通用权限管理系统源代码开源发布v1.1', '1', '系统追求原始精简的原则在执行效率上更快，采用bootstrap为前台开发展示UI，Web Api主要负责前端的逻辑交互，再结合jQuery Ajax+Web Api进行提交数据请求，系统免费开源。', '<p>一、Angel工作室简单通用权限系统简介</p>\n<p>&nbsp;&nbsp;&nbsp;AngelRM（Asp.net MVC）是基于asp.net(C#)MVC打造后端原生态代码+前端bootstrap+ztree+lodash+jquery技术，系统追求原始精简的原则在执行效率上更快，采用bootstrap为前台开发展示UI，Web Api主要负责前端的逻辑交互，再结合jQuery Ajax+Web Api进行提交数据请求。</p>\n<p>二、框架特色</p>\n<p>1、为了数据方便读写，语言的成熟性，选择asp.net mvc开发效率更高效率更快；</p>\n<p>2、系统架构采用：耦合性低、重用性高、部署快、可维护性高等优点的MVC框架进行搭建；</p>\n<p>3、系统配置文件数据采用HttpRuntime.Cache进行缓存，使得程序在运行中效率更高、速度更快；</p>\n<p>4、数据库的选型，根据系统的数据规模与需求方的相关要求，综合多方考量，调研多种数据库后选定mysql为系统数据库；</p>\n<p>5、运行环境根据功能模块的特点，选型window server2008+IIS，部署更简洁；</p>\n<p>6、使用报表导出开源组件NPOI可以在没有安装Office的情况下对Word或Excel文档进行读写操作；</p>\n<p>7、为了兼容更多的浏览器，让用户界面使用更友好。我们选择了响应式布局框架bootstrap；</p>\n<p>8、系统报表我们采用了echarts开源软件，并且我们提供了非常炫酷的图形界面，特色是地图，另外还提供了柱状图、折线图、饼图、气泡图及四象限图等；</p>\n<p>9、系统采用bootstrap响应式布局，这样面对不同分辨率设备灵活性强能够快捷解决多设备显示适应问题；</p>\n<p>10、系统基于ASP.NET(C#) MVC +web api+Bootstrap +Jquery+ MYSQL/SQL SERVER前端采用响应式布局对页面兼容性显著提高；</p>\n<p>系统优势：</p>\n<p>代码可以满足大部分开发者的需求，让开发人员能省不少时间</p>\n<p>开源下载地址&nbsp;</p>\n<p>Gitee地址：https://gitee.com/angelcms/AngelRM_MVC</p>\n<p>系统演示图：</p>\n<p><img src=\"https://img2020.cnblogs.com/blog/516621/202105/516621-20210518134027451-806863202.jpg\" alt=\"\"></p>\n<p><img src=\"https://img2020.cnblogs.com/blog/516621/202105/516621-20210518134231220-1662563360.jpg\" alt=\"\"></p>\n<p><img src=\"https://img2020.cnblogs.com/blog/516621/202105/516621-20210518134258538-2109971796.jpg\" alt=\"\"></p>\n<p><img src=\"https://img2020.cnblogs.com/blog/516621/202105/516621-20210518134325270-1227113377.jpg\" alt=\"\"></p>\n<p><img src=\"https://img2020.cnblogs.com/blog/516621/202105/516621-20210518134339388-1285948521.jpg\" alt=\"\"></p>\n<p><img src=\"https://img2020.cnblogs.com/blog/516621/202105/516621-20210518134404043-156779797.jpg\" alt=\"\"></p>\n<p>等等还有一些页面我就不一一上传了</p>\n<p><br></p>', NULL, NULL, '/images/cover/1561132050443308.jpg', '1', 5, '0', 0, 0, 10, 1, 0, '1', 0, NULL, '1', 'alcedo', '2021-11-23 19:40:35', '2022-04-30 21:49:08', 0, NULL);
INSERT INTO `cms_resource` VALUES ('2021112319403531361648', 'recommend', 'ASP.NET MVC通用权限管理系统源代码开源发布v1.0', '1', '系统追求原始精简的原则在执行效率上更快，采用bootstrap为前台开发展示UI，Web Api主要负责前端的逻辑交互，再结合jQuery Ajax+Web Api进行提交数据请求，系统免费开源。', '<p>一、Angel工作室简单通用权限系统简介</p>\n<p>&nbsp;&nbsp;&nbsp;AngelRM（Asp.net MVC）是基于asp.net(C#)MVC打造后端原生态代码+前端bootstrap+ztree+lodash+jquery技术，系统追求原始精简的原则在执行效率上更快，采用bootstrap为前台开发展示UI，Web Api主要负责前端的逻辑交互，再结合jQuery Ajax+Web Api进行提交数据请求。</p>\n<p>二、框架特色</p>\n<p>1、为了数据方便读写，语言的成熟性，选择asp.net mvc开发效率更高效率更快；</p>\n<p>2、系统架构采用：耦合性低、重用性高、部署快、可维护性高等优点的MVC框架进行搭建；</p>\n<p>3、系统配置文件数据采用HttpRuntime.Cache进行缓存，使得程序在运行中效率更高、速度更快；</p>\n<p>4、数据库的选型，根据系统的数据规模与需求方的相关要求，综合多方考量，调研多种数据库后选定mysql为系统数据库；</p>\n<p>5、运行环境根据功能模块的特点，选型window server2008+IIS，部署更简洁；</p>\n<p>6、使用报表导出开源组件NPOI可以在没有安装Office的情况下对Word或Excel文档进行读写操作；</p>\n<p>7、为了兼容更多的浏览器，让用户界面使用更友好。我们选择了响应式布局框架bootstrap；</p>\n<p>8、系统报表我们采用了echarts开源软件，并且我们提供了非常炫酷的图形界面，特色是地图，另外还提供了柱状图、折线图、饼图、气泡图及四象限图等；</p>\n<p>9、系统采用bootstrap响应式布局，这样面对不同分辨率设备灵活性强能够快捷解决多设备显示适应问题；</p>\n<p>10、系统基于ASP.NET(C#) MVC +web api+Bootstrap +Jquery+ MYSQL/SQL SERVER前端采用响应式布局对页面兼容性显著提高；</p>\n<p>系统优势：</p>\n<p>代码可以满足大部分开发者的需求，让开发人员能省不少时间</p>\n<p>开源下载地址&nbsp;</p>\n<p>Gitee地址：https://gitee.com/angelcms/AngelRM_MVC</p>\n<p>系统演示图：</p>\n<p><img src=\"https://img2020.cnblogs.com/blog/516621/202105/516621-20210518134027451-806863202.jpg\" alt=\"\"></p>\n<p><img src=\"https://img2020.cnblogs.com/blog/516621/202105/516621-20210518134231220-1662563360.jpg\" alt=\"\"></p>\n<p><img src=\"https://img2020.cnblogs.com/blog/516621/202105/516621-20210518134258538-2109971796.jpg\" alt=\"\"></p>\n<p><img src=\"https://img2020.cnblogs.com/blog/516621/202105/516621-20210518134325270-1227113377.jpg\" alt=\"\"></p>\n<p><img src=\"https://img2020.cnblogs.com/blog/516621/202105/516621-20210518134339388-1285948521.jpg\" alt=\"\"></p>\n<p><img src=\"https://img2020.cnblogs.com/blog/516621/202105/516621-20210518134404043-156779797.jpg\" alt=\"\"></p>\n<p>等等还有一些页面我就不一一上传了</p>\n<p><br></p>\n<p><br></p>', '', '', '/images/cover/1561132050443308.jpg', '1', 5, '0', 0, 0, 10, 1, 0, '1', 0, '', '1', 'alcedo', '2021-11-23 19:40:35', '2022-04-30 21:50:00', 0, '');
INSERT INTO `cms_resource` VALUES ('2021112319493222711819', 'recommend', 'ASP.NET MVC通用权限管理系统源代码开源发布v1.1', '1', '系统追求原始精简的原则在执行效率上更快，采用bootstrap为前台开发展示UI，Web Api主要负责前端的逻辑交互，再结合jQuery Ajax+Web Api进行提交数据请求，系统免费开源。', '<p>一、Angel工作室简单通用权限系统简介</p>\n<p>&nbsp;&nbsp;&nbsp;AngelRM（Asp.net MVC）是基于asp.net(C#)MVC打造后端原生态代码+前端bootstrap+ztree+lodash+jquery技术，系统追求原始精简的原则在执行效率上更快，采用bootstrap为前台开发展示UI，Web Api主要负责前端的逻辑交互，再结合jQuery Ajax+Web Api进行提交数据请求。</p>\n<p>二、框架特色</p>\n<p>1、为了数据方便读写，语言的成熟性，选择asp.net mvc开发效率更高效率更快；</p>\n<p>2、系统架构采用：耦合性低、重用性高、部署快、可维护性高等优点的MVC框架进行搭建；</p>\n<p>3、系统配置文件数据采用HttpRuntime.Cache进行缓存，使得程序在运行中效率更高、速度更快；</p>\n<p>4、数据库的选型，根据系统的数据规模与需求方的相关要求，综合多方考量，调研多种数据库后选定mysql为系统数据库；</p>\n<p>5、运行环境根据功能模块的特点，选型window server2008+IIS，部署更简洁；</p>\n<p>6、使用报表导出开源组件NPOI可以在没有安装Office的情况下对Word或Excel文档进行读写操作；</p>\n<p>7、为了兼容更多的浏览器，让用户界面使用更友好。我们选择了响应式布局框架bootstrap；</p>\n<p>8、系统报表我们采用了echarts开源软件，并且我们提供了非常炫酷的图形界面，特色是地图，另外还提供了柱状图、折线图、饼图、气泡图及四象限图等；</p>\n<p>9、系统采用bootstrap响应式布局，这样面对不同分辨率设备灵活性强能够快捷解决多设备显示适应问题；</p>\n<p>10、系统基于ASP.NET(C#) MVC +web api+Bootstrap +Jquery+ MYSQL/SQL SERVER前端采用响应式布局对页面兼容性显著提高；</p>\n<p>系统优势：</p>\n<p>代码可以满足大部分开发者的需求，让开发人员能省不少时间</p>\n<p>开源下载地址&nbsp;</p>\n<p>Gitee地址：https://gitee.com/angelcms/AngelRM_MVC</p>\n<p>系统演示图：</p>\n<p><img src=\"https://img2020.cnblogs.com/blog/516621/202105/516621-20210518134027451-806863202.jpg\" alt=\"\"></p>\n<p><img src=\"https://img2020.cnblogs.com/blog/516621/202105/516621-20210518134231220-1662563360.jpg\" alt=\"\"></p>\n<p><img src=\"https://img2020.cnblogs.com/blog/516621/202105/516621-20210518134258538-2109971796.jpg\" alt=\"\"></p>\n<p><img src=\"https://img2020.cnblogs.com/blog/516621/202105/516621-20210518134325270-1227113377.jpg\" alt=\"\"></p>\n<p><img src=\"https://img2020.cnblogs.com/blog/516621/202105/516621-20210518134339388-1285948521.jpg\" alt=\"\"></p>\n<p><img src=\"https://img2020.cnblogs.com/blog/516621/202105/516621-20210518134404043-156779797.jpg\" alt=\"\"></p>\n<p>等等还有一些页面我就不一一上传了</p>\n<p><br></p>', NULL, NULL, '/images/cover/1555918524581196.jpg', '1', 5, '0', 0, 0, 8, 4, 0, '1', 0, NULL, '1', 'alcedo', '2021-11-23 19:49:32', '2022-04-30 21:50:46', 1, NULL);
INSERT INTO `cms_resource` VALUES ('2021122011401854648291', 'recommend', 'ASP.NET MVC通用权限管理系统源代码开源发布v1.2', '1', '系统追求原始精简的原则在执行效率上更快，采用bootstrap为前台开发展示UI，Web Api主要负责前端的逻辑交互，再结合jQuery Ajax+Web Api进行提交数据请求，系统免费开源。', '<p>一、asp.net mvc&nbsp;通用权限管理系统(响应布局)源码主要以下特点：</p>\n<p>&nbsp;&nbsp;&nbsp;AngelRM（Asp.net MVC）是基于asp.net(C#)MVC+前端bootstrap+ztree+lodash+jquery技术，采用bootstrap为前台开发展示UI，Web Api主要负责前端的逻辑交互，再结合jQuery Ajax+Web Api进行提交数据请求。</p>\n<p>二、框架特色</p>\n<p>1、为了数据方便读写，语言的成熟性，选择asp.net开发效率更高效率更快；<br>2、系统架构采用：耦合性低、重用性高、部署快、可维护性高等优点的MVC框架进行搭建；<br>3、系统配置文件数据采用HttpRuntime.Cache进行缓存，使得程序在运行中效率更高、速度更快；<br>4、数据库的选型，根据系统的数据规模与需求方的相关要求，综合多方考量，调研多种数据库后选定mysql为系统数据库；<br>5、运行环境根据功能模块的特点，选型window server2008+IIS，部署更简洁；<br>6、使用报表导出开源组件NPOI可以在没有安装Office的情况下对Word或Excel文档进行读写操作；<br>7、为了兼容更多的浏览器，让用户界面使用更友好。我们选择了响应式布局框架bootstrap；<br>8、系统报表我们采用了echarts开源软件，并且我们提供了非常炫酷的图形界面，特色是地图，另外还提供了柱状图、折线图、饼图、气泡图及四象限图等；<br>9、系统采用bootstrap响应式布局，这样面对不同分辨率设备灵活性强能够快捷解决多设备显示适应问题；</p>\n<p>&nbsp;系统优势：</p>\n<p>代码可以满足大部分开发者的需求，让开发人员能省不少时间。前两天有的小伙伴说我发布的系统图片不太清晰，接下来发几张高清图</p>\n<p>三、界面展示如下：</p>\n<p>2.0较1.0版本新增和更新了一些代码</p>\n<p>1、新增文件上传功能实例，保证文件能够无误上传保存并下载；</p>\n<p>2、优化后台系统框架代码，删除以前大项目其它无用功能；</p>\n<p>3、新增页面代码JS、css文件的压缩，使得系统运行速度更快；</p>\n<p>4、系统登录页面新增验证校验过程，这样做到输入验证码时时验证；</p>\n<p>5、优化系统加载页面提示数据加载功能。</p>\n<p>登录页面：</p>\n<p><img src=\"https://img2018.cnblogs.com/common/516621/202002/516621-20200227133609133-1833734207.jpg\" alt=\"\"></p>\n<p>主界面：</p>\n<p><img src=\"https://img2018.cnblogs.com/common/516621/202002/516621-20200227133654250-569018534.jpg\" alt=\"\"></p>\n<p>菜单管理列表：</p>\n<p><img src=\"https://img2018.cnblogs.com/common/516621/202002/516621-20200227133929796-1632802414.jpg\" alt=\"\"></p>\n<p>部门管理：</p>\n<p><img src=\"https://img2018.cnblogs.com/common/516621/202002/516621-20200227134002922-815873456.jpg\" alt=\"\"></p>\n<p>上传文件：</p>\n<p><img src=\"https://img2018.cnblogs.com/common/516621/202002/516621-20200227134039125-118528278.jpg\" alt=\"\"></p>\n<p><em>角色管理：</em></p>\n<p>&nbsp;</p>\n<p><em><img src=\"https://img2018.cnblogs.com/common/516621/202002/516621-20200227134117933-130558068.jpg\" alt=\"\"><br></em></p>\n<p><em>下载页面管理：</em></p>\n<p><em><img src=\"https://img2018.cnblogs.com/common/516621/202002/516621-20200227134147153-395450699.jpg\" alt=\"\"></em></p>\n<p><em><img src=\"https://img2018.cnblogs.com/common/516621/202002/516621-20200227134208399-1028963959.jpg\" alt=\"\"></em></p>\n<p><em><img src=\"https://img2018.cnblogs.com/common/516621/202002/516621-20200227134220918-925256055.jpg\" alt=\"\"></em></p>\n<p><em>等等还有一些页面我就不一一上传了</em></p>\n<p>技术交流可加QQ群：97157124</p>\n<p>Gitee地址：<a href=\"https://gitee.com/angelcms/AngelRM_MVC\">https://gitee.com/angelcms/AngelRM_MVC</a></p>\n<p><br></p>', NULL, NULL, '/images/cover/1561132050443308.jpg', '1', 2, '0', 0, 0, 4, 0, 0, '1', 0, NULL, '1', 'alcedo', '2021-12-20 11:40:19', '2022-04-30 21:51:24', 0, NULL);
